<?phpfunction pipeSendMethodState($state){    switch ($state){        case 'Active':            echo "<span class='label label-success'>فعال</span>";            break;        case 'InActive':            echo "<span class='label label-danger'>غیرفعال</span>";            break;    }}function pipeDiscountCodeType($discountType){    switch ($discountType){        case 'GeneralBase':            echo "<span class='label label-default'>عمومی</span>";            break;        case 'CategoryBase':            echo "<span class='label label-danger'>دسته بندی</span>";            break;        case 'ProductBase':            echo "<span class='label label-danger'>تک محصول</span>";            break;        case 'UserBase':            echo "<span class='label label-danger'>تک کاربر</span>";            break;    }}function pipeCategoryIsFavorite($input){    switch ($input){        case 0:            echo '<i class="material-icons">star</i>';            break;        case 1:            echo '<i class="material-icons" style="color: orangered;">star</i>';            break;    }}function getCategoryTreeOptions($array, $parent=0, $indent="") {    $return = array();    foreach($array as $key => $val) {        if($val["CategoryParentId"] == $parent) {            $return["x".$val["CategoryId"]] = $indent.$val["CategoryTitle"];            $return = array_merge($return, getCategoryTreeOptions($array, $val["CategoryId"], $indent."&nbsp;&nbsp;&nbsp;"));        }    }    return $return;}?>