<?phpfunction seoUrl($string) {    return str_ireplace(" ","-" , $string);}function productUrl($productId , $productTitle) {    return base_url('Product/detail/'.$productId.'/'.str_ireplace(" ","-" , $productTitle));}function pipeEnum($enumName, $k, $class = "label-warning"){    $ci =& get_instance();    foreach ($ci->config->item($enumName) as $key => $value) {        if ($key == $k) {            echo $value;        }    }}function showProductPrice($prices , $productType){    switch ($productType){        case 'Normal':            echo number_format($prices[0]['PriceValue']).' <b class="unit">تومان</b>';            break;        case 'NormalUpload':            echo number_format($prices[0]['PriceValue']).' <b class="unit">تومان</b>';            break;        case 'DesignFixSize':            echo "شروع از ".number_format($prices[0]['PriceValue']).' <b class="unit">تومان</b>';            break;        case 'DesignFreeSize':            echo "شروع از ".number_format($prices[0]['PriceValue']).' <b class="unit">تومان</b>';            break;    }}function categoryUrl($categoryId , $categoryTitle) {    return base_url('Category/detail/'.$categoryId.'/'.str_ireplace(" ","-" , $categoryTitle));}function setSpecialBadge($isSpecial){    if($isSpecial){        echo '<div class="circle"><p>پیشنهاد ویژه</p></div>';    }}function setTypeBadge($type){    switch ($type){        case 'Normal':            break;        case 'NormalUpload':            echo "<i class='fa fa-upload icon-badge'></i>";            break;        case 'DesignFreeSize':            echo "<i class='fa fa-upload icon-badge'></i>";            echo "<i class='fa fa-expand icon-badge'></i>";            break;        case 'DesignFixSize':            echo "<i class='fa fa-upload icon-badge'></i>";            echo "<i class='fa fa-crop icon-badge'></i>";            break;    }}function productTypePipe($type) {    switch ($type){        case 'Normal':            echo "معمولی";            break;        case 'NormalUpload':            echo "معمولی / قابلیت آپلود";            break;        case 'DesignFreeSize':            echo "طراحی با سایز متغیر";            break;        case 'DesignFixSize':            echo "طراحی با سایز ثابت";            break;    }}function orderStatusPipe($type) {    switch ($type){        case 'Pend':            echo "<label class='label label-default'>در انتظار پرداخت</label>";            break;        case 'Failed':            echo "<label class='label label-danger'>انصراف از پرداخت</label>";            break;        case 'Done':            echo "<label class='label label-success'>پرداخت شده</label>";            break;    }}function pipeYesNo($type) {    switch ($type){        case 0:            echo "<label>خیر</label>";            break;        case 1:            echo "<label>بله</label>";            break;    }}function orderCommentStatusPipe($type) {    switch ($type){        case 'Pend':            echo "<label class='label label-default'>در انتظار تایید</label>";            break;        case 'Accept':            echo "<label class='label label-success'>تایید شده</label>";            break;    }}function getLoggedUserId(){    $ci =& get_instance();    if($ci->session->userdata('UserIsLogged')){        return $ci->session->userdata('UserLoginInfo')[0]['UserId'];    }}function sendSMS($to ,$code ,  $inputMessage){    try {        //$to = '09120572107';        $options = [            'cache_wsdl' => WSDL_CACHE_NONE,            'trace' => 1,            'stream_context' => stream_context_create(                [                    'ssl' => [                        'verify_peer' => false,                        'verify_peer_name' => false,                        'allow_self_signed' => true                    ]                ]            )        ];        ini_set("soap.wsdl_cache_enabled", "0");        $client = new SoapClient("http://188.0.240.110/class/sms/wsdlservice/server.php?wsdl", $options);        $user = "miladghelich";        $pass = "Zibadesign64Ghelich";        $fromNum = "+983000505";        $toNum = array($to);        $pattern_code = $code;        $input_data = $inputMessage;        return $client->sendPatternSms($fromNum, $toNum, $user, $pass, $pattern_code, $input_data);    } catch (Exception $e){}}function FaToEn($input){    return strtr($input, array('۰' => '0', '۱' => '1', '۲' => '2', '۳' => '3', '۴' => '4', '۵' => '5', '۶' => '6', '۷' => '7', '۸' => '8', '۹' => '9', '٠' => '0', '١' => '1', '٢' => '2', '٣' => '3', '٤' => '4', '٥' => '5', '٦' => '6', '٧' => '7', '٨' => '8', '٩' => '9'));}function convertDate($input, $isHtml = false){    if ($input !== NULL) {        if ($isHtml) {            return jDateTime::date("Y/m/d", $input, false, true, 'Asia/Tehran');        } else {            return '<span style="direction:rtl;">' . jDateTime::date("Y/m/d", $input, false, true, 'Asia/Tehran') . '</span>';        }    } else {        return "";    }}function convertTime($input, $isHtml = false){    if ($input !== NULL) {        if ($isHtml) {            return jDateTime::date("H:i:s", $input, false, true, 'Asia/Tehran');        } else {            return '<span style="direction:rtl;">' . jDateTime::date("H:i:s", $input, false, true, 'Asia/Tehran') . '</span>';        }    } else {        return "";    }}function makeTime($input){    if ($input !== NULL && $input != '') {        $input = explode('/', $input);        return jDateTime::mktime(0, 0, 0, $input[1], $input[2], $input[0]);    }}function randomString($type = 'numeric', $length = 10){    return random_string($type, $length);}function setInputValue($input){    echo "value='" . $input . "'";}function setOptionSelected($input, $compare){    if ($input == $compare) echo "selected";}function setRadioSelected($input, $compare){    if ($input == $compare) echo "checked";}function setSelectData($input, $currentValue = NULL){    foreach ($input as $key => $value) {        if ($currentValue != NULL && $key == $currentValue) {            echo "<option value='" . $key . "' selected>";        } else {            echo "<option value='" . $key . "'>";        }        echo $value;        echo "</option>";    }}?>