<?phpfunction seoUrl($string) {    return str_ireplace(" ","-" , $string);}function productUrl($productId , $productTitle) {    return base_url('Product/detail/'.$productId.'/'.str_ireplace(" ","-" , $productTitle));}function categoryUrl($categoryId , $categoryTitle) {    return base_url('Category/detail/'.$categoryId.'/'.str_ireplace(" ","-" , $categoryTitle));}function productTypePipe($type) {    switch ($type){        case 'Normal':            echo "معمولی";            break;        case 'NormalUpload':            echo "معمولی / قابلیت آپلود";            break;        case 'DesignFreeSize':            echo "طراحی با سایز متغیر";            break;        case 'DesignFixSize':            echo "طراحی با سایز ثابت";            break;    }}function orderStatusPipe($type) {    switch ($type){        case 'Pend':            echo "در انتظار پرداخت";            break;        case 'Failed':            echo "انصراف از پرداخت";            break;        case 'Done':            echo "پرداخت شده";            break;    }}function getLoggedUserId(){    $ci =& get_instance();    if($ci->session->userdata('UserIsLogged')){        return $ci->session->userdata('UserLoginInfo')[0]['UserId'];    }}?>