<?phpfunction seoUrl($string) {    return str_ireplace(" ","-" , $string);}function productUrl($productId , $productTitle) {    return base_url('Product/detail/'.$productId.'/'.str_ireplace(" ","-" , $productTitle));}function showProductPrice($prices , $productType){    switch ($productType){        case 'Normal':            echo number_format($prices[0]['PriceValue']);            break;        case 'NormalUpload':            echo number_format($prices[0]['PriceValue']);            break;        case 'DesignFixSize':            echo "شروع از ".number_format($prices[0]['PriceValue']);            break;        case 'DesignFreeSize':            echo "هر سانتی متر مربع ".number_format($prices[0]['PriceValue']);            break;    }}function categoryUrl($categoryId , $categoryTitle) {    return base_url('Category/detail/'.$categoryId.'/'.str_ireplace(" ","-" , $categoryTitle));}function productTypePipe($type) {    switch ($type){        case 'Normal':            echo "معمولی";            break;        case 'NormalUpload':            echo "معمولی / قابلیت آپلود";            break;        case 'DesignFreeSize':            echo "طراحی با سایز متغیر";            break;        case 'DesignFixSize':            echo "طراحی با سایز ثابت";            break;    }}function orderStatusPipe($type) {    switch ($type){        case 'Pend':            echo "<label class='label label-default'>در انتظار پرداخت</label>";            break;        case 'Failed':            echo "<label class='label label-danger'>انصراف از پرداخت</label>";            break;        case 'Done':            echo "<label class='label label-success'>پرداخت شده</label>";            break;    }}function getLoggedUserId(){    $ci =& get_instance();    if($ci->session->userdata('UserIsLogged')){        return $ci->session->userdata('UserLoginInfo')[0]['UserId'];    }}function sendSMS($to ,$code ,  $inputMessage){    $client = new SoapClient("http://188.