<?phpfunction seoUrl($string) {    return str_ireplace(" ","-" , $string);}function productUrl($productId , $productTitle) {    return base_url('Product/detail/'.$productId.'/'.str_ireplace(" ","-" , $productTitle));}function showProductPrice($prices , $productType){    switch ($productType){        case 'Normal':            echo number_format($prices[0]['PriceValue']).' <b class="unit">تومان</b>';            break;        case 'NormalUpload':            echo number_format($prices[0]['PriceValue']).' <b class="unit">تومان</b>';            break;        case 'DesignFixSize':            echo "شروع از ".number_format($prices[0]['PriceValue']).' <b class="unit">تومان</b>';            break;        case 'DesignFreeSize':            //echo "شروع از ".number_format($prices[0]['PriceValue']);            break;    }}function categoryUrl($categoryId , $categoryTitle) {    return base_url('Category/detail/'.$categoryId.'/'.str_ireplace(" ","-" , $categoryTitle));}function setSpecialBadge($isSpecial){    if($isSpecial){        echo '<div class="circle"><p>پیشنهاد ویژه</p></div>';    }}function setTypeBadge($type){    switch ($type){        case 'Normal':            break;        case 'NormalUpload':            echo "<i class='fa fa-upload icon-badge'></i>";            break;        case 'DesignFreeSize':            echo "<i class='fa fa-upload icon-badge'></i>";            echo "<i class='fa fa-expand icon-badge'></i>";            break;        case 'DesignFixSize':            echo "<i class='fa fa-upload icon-badge'></i>";            echo "<i class='fa fa-crop icon-badge'></i>";            break;    }}function productTypePipe($type) {    switch ($type){        case 'Normal':            echo "معمولی";            break;        case 'NormalUpload':            echo "معمولی / قابلیت آپلود";            break;        case 'DesignFreeSize':            echo "طراحی با سایز متغیر";            break;        case 'DesignFixSize':            echo "طراحی با سایز ثابت";            break;    }}function orderStatusPipe($type) {    switch ($type){        case 'Pend':            echo "<label class='label label-default'>در انتظار پرداخت</label>";            break;        case 'Failed':            echo "<label class='label label-danger'>انصراف از پرداخت</label>";            break;        case 'Done':            echo "<label class='label label-success'>پرداخت شده</label>";            break;    }}function getLoggedUserId(){    $ci =& get_instance();    if($ci->session->userdata('UserIsLogged')){        return $ci->session->userdata('UserLoginInfo')[0]['UserId'];    }}function sendSMS($to ,$code ,  $inputMessage){    $client = new SoapClient("http://ippanel.com/class/sms/wsdlservice/server.php?wsdl");    $user = "miladghelich";    $pass = "Zibadesign64Ghelich";    $fromNum = "+9810009589";    $toNum = array($to);    $pattern_code = $code;    $input_data = $inputMessage;    return $client->sendPatternSms($fromNum,$toNum,$user,$pass,$pattern_code,$input_data);}?>